Page.content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
a {
  text-decoration: none;
}
a:link {
  color: #000;
}
a:visited {
  color: #000;
}
a:hover {
  color: #a52a2a;
}
a:active {
  color: #008000;
}
.header-container {
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 8vh;
  width: 100%;
  background: linear-gradient(to right, #27374d, #dde6ed);
}
.header-container .nav-left a:link {
  color: #212223;
}
.header-container .nav-left a:visited {
  color: #212223;
}
.header-container .nav-left a:hover {
  color: #212223;
}
.header-container .nav-left a:active {
  color: #212223;
}
.header-container .nav-right {
  list-style: none;
  display: flex;
  height: 100%;
  width: 30%;
  justify-content: space-evenly;
  align-items: center;
}
.header-container .nav-right li {
  display: inline-block;
  color: #fff;
}
.header-container .nav-right a {
  font-size: 1.5em;
  text-decoration: none;
}
.header-container .nav-right a:link {
  color: #27374d;
}
.header-container .nav-right a:visited {
  color: #27374d;
}
.header-container .nav-right a:hover {
  color: #526d82;
}
.header-container .nav-right a:active {
  color: #27374d;
}
